package binaryopeators;

public class RealationalAndCompaisionOperators {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int a=10;
		int b=20;
		//this operators will compare the operands values it returns boolean outputs
		//These are ==,!=,<=,>=,<,>
		System.out.println(a==b);//false
		System.out.println(a!=b);//true
		System.out.println(a>=b);//false
		System.out.println(a<=b);//true
		System.out.println(a>b);//false
		System.out.println(a<b);//true

	}

}
