package operators;

public class PostDecremntUsingTwoVariables {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int a=10;
		int b;
		b=a--;
		/*In post decrement first assign the value to the different
		 * variable and then decrement value by one
		 */
		System.out.println(a);//9
		System.out.println(b);//10

	}

}
