package operators;

public class PreDecrementUsingTwoVariables {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int a=10;
		int b;
		b=--a;
		/*In pre decrement first perform decrement then assign
		the value to different variable*/
		System.out.println(a);//9
		System.out.println(b);//9

	}

}
