package operators;

public class PreIncrementUsingTwoOperands {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int a=10;
		int b;
		b=++a;
		/*here first we increment the value and then assign the 
		value to different variable*/
		System.out.println(a);//11
		System.out.println(b);//11

	}

}
